(function (window) {

	var pi = '3.';
	var e = '2.';
	var ln2 = '0.';

	var sqrt1_2 = converter.Big(1/2).sqrt() + '';
	var sqrt1_3 = converter.Big(1).div(3).sqrt() + '';
	var sqrt2 = converter.Big(2).sqrt() + '';
	var sqrt3 = converter.Big(3).sqrt() + '';
	var sqrt5 = converter.Big(5).sqrt() + '';
	var sqrt7 = converter.Big(7).sqrt() + '';
	var pi_2 = converter.Big(pi).mul(pi) + '';
	var tau = converter.Big(pi).mul(2) + '';
	var golden = converter.Big(1).add(sqrt5).div(2) + '';

	var CALCULATION_EXAMPLES_DEFAULT = window.CALCULATION_EXAMPLES_DEFAULT = [
		{ name: 'pi (π)', numberDisplay: '3.14', number: pi },
		{ name: 'e', numberDisplay: '2.718', number: e },
		{ name: 'golden ratio (φ)', numberDisplay: '1.618', number: golden },
		{ name: '√2', numberDisplay: '1.414', number: sqrt2 },
		{ name: 'tau (τ = 2π)', numberDisplay: '6.28', number: tau },
	];


	var CALCULATION_EXAMPLES = window.CALCULATION_EXAMPLES = [
		{ name: 'pi (π)', numberDisplay: '3.14', number: pi },
		{ name: 'e', numberDisplay: '2.718', number: e },
		{ name: 'golden ratio (φ)', numberDisplay: '1.618', number: golden },
		{ name: '√2', numberDisplay: '1.414', number: sqrt2 },
		{ name: 'tau (τ = 2π)', numberDisplay: '6.28', number: tau },

		{ name: '√½', numberDisplay: '0.707', number: sqrt1_2 },
		{ name: '√⅓', numberDisplay: '0.577', number: sqrt1_3 },
		{ name: '√3', numberDisplay: '1.732', number: sqrt3 },
		{ name: '√5', numberDisplay: '2.236', number: sqrt5 },
		{ name: '√7', numberDisplay: '2.646', number: sqrt7 },
		{ name: 'pi squared (π²)', numberDisplay: '9.87', number: pi_2 },
		{ name: 'ln 2', numberDisplay: '0.693', number: ln2 },
	];

	CALCULATION_EXAMPLES.forEach(fix);
	CALCULATION_EXAMPLES_DEFAULT.forEach(fix);

	function fix(base) {
		base.baseId = '10';
		base.number = base.number.replace(/[0-9]{5}(?=[0-9])/g, '$& ');
	}

}(window));